mapper.update:
update tb_sys_user set
  flag = #{flag}
where id = #{id}

mapper.select:
select t.id, t.dept_id, t.nickname, t.sex, t.age, t.pwd, t.account, t.phone, t.email,
t.idnumber, t.address, t.money, t.integral, t.head, t.flag, t.create_time, t.remark, tm.name deptName
from tb_sys_user t
left join tb_sys_dept tm on t.dept_id = tm.id


        and t.flag = #{flag}




        and (t.idnumber = #{idnumber}


        or t.account = #{account}


        or t.nickname = #{nickname}


        or t.phone = #{phone})


order by id desc
